import React from "react";


import { CurrentUserContext } from "../context/CurrentUserContext";



function Card({ key, card, onCardClick }) {

const currentUser = React.useContext(CurrentUserContext);
const isOwn = card.owner._id === currentUser._id;

const cardDeleteButtonClassName = (
  `place__delete ${isOwn ? 'card__delete-button_visible' : 'card__delete-button_hidden'}`
); 

  function handleClick() {
    onCardClick(card);
  }

const isLiked = card.likes.some(i => i._id === currentUser._id);
debugger
const cardLikeButtonClassName = `place__like ${
  isLiked && "place__like_active"
}`;



  return (
    <li className="place" key={key}>
      <img
        className="place__foto"
        style={{ backgroundImage: `url(${card.link})` }}
        onClick={handleClick}
      />
      <button type="button" className={cardDeleteButtonClassName}/>
      <div className="place__rectangle">
        <h2 className="place__title">{card.name}</h2>
        <div className="place__like-place">
          <button type="button" className={cardLikeButtonClassName} />
          <span className="place__like-count">{card.likes.length}</span>
        </div>
      </div>
    </li>
  );
}

export default Card;
