import api from "../utils/api";
import React, { useState } from "react";
import Card from "./Card";
import { CurrentUserContext } from "../context/CurrentUserContext";

function Main({
  onEditProfile,
  onAddPlace,
  onEditAvatar,
  onCardClick
}) {
  const currentUser = React.useContext(CurrentUserContext);

  const [cards, setCards] = React.useState([]);
  const [cardDel, setCardDel] = React.useState(null);

  React.useEffect(() => {
    api
      .gerCards()
      .then((cards) => {
        setCards(cards);
      })
      .catch((err) => console.log("Ошибка получения карточек" + err));
  }, []);


  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some((i) => i._id === currentUser._id);

    // Отправляем запрос в API и получаем обновлённые данные карточки
    api.likeCard(card._id, !isLiked).then((newCard) => {
      setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
    });
  }

//доделать + отображение удаления не на всех
  function handleCardDelete(evt) {
    api.deleteCard(cardDel._id)
      .then(() => {
        setCards((state) => state.filter((c) => c._id !== cardDel._id));
      })
      .catch(err => console.log(`При удалении карточки: ${err}`))
  }


  return (
    <main className="content">
      <section className="profile">
        <div className="profile__avatar-container">
          <img
            className="profile__avatar"
            onClick={onEditAvatar}
            src={currentUser.avatar}
          />
        </div>
        <div className="profile__info">
          <h1 className="profile__name">{currentUser.name}</h1>
          <p className="profile__description">{currentUser.about}</p>
          <button
            type="button"
            className="profile__edit-button"
            onClick={onEditProfile}
          />
        </div>
        <button
          type="button"
          className="profile__add-button"
          onClick={onAddPlace}
        />
      </section>
      <section className="places">
        <ul className="places__grid">
          {cards.map((card) => (
            <Card
              key={card._id}
              card={card}
              onCardClick={onCardClick}
              onCardLike={handleCardLike}
              onCardDelete={handleCardDelete}
            />
          ))}
        </ul>
      </section>
    </main>
  );
}

export default Main;
